dot(dots);

label("(5,2)",A,E);

draw((-3,-6)--(-3,10),linetype("3 3"),Arrows(4));

draw(B--A--C);

label("10",(A+B)/2,NE);

label("10",(A+C)/2,SE);

label("$x=-3$",(-3,-6),S);[/asy]

