label("$B$",B,E);
label("$C$",C,E);
label("$D$",D,W);
label("$E$",I,N);
label("$F$",F,S);
label("$G$",G,S);
label("$H$",H,NW);
label("$J$",J,dir(70));
draw(H--G,dashed);
